const Item = ({ id, note, date, time, deleteData }) => {
  function deleteItem() {
    deleteData(function (prevData) {
      return prevData.filter((item) => item.id !== id);
    });
  }

  return (
    <div className="item">
      <span>{note}</span>
      <p>{`${date} ${time}`}</p>
      <button onClick={deleteItem} className="remove">
        刪除
      </button>
    </div>
  );
};

export default Item;
