import Pic from "../../../images/default-card-picture.png";
import React from "react";
import { useState, useEffect, useRef } from "react";
import { axiosCall, formatDate } from '../controller/tool'

const CarouselCard = ({ activity }) => {
    const { ID, ScenicSpotName, Address, Picture = {} } = activity;
    const { PictureUrl1 = '', PictureDescription1 = '' } = Picture;
    const AddressP = Address.substr(0, 3);
    return (
        <div>
            {/* <div className="carousel-item active">
                <p className="picName">name</p>
                <img src={PictureUrl1} className="d-block w-100" alt="..."/>
            </div> */}
            <div className="carousel-item">
                <p className="picName">{ScenicSpotName} | {AddressP}</p>
                <img src={PictureUrl1} className="d-block w-100" alt={PictureDescription1}/>
            </div>
            {/* <div className="carousel-item">
                <p className="picName">name2</p>
                <img src={PictureUrl1} className="d-block w-100" alt="..."/>
            </div>
            <div className="carousel-item">
                <p className="picName">name</p>
                <img src={PictureUrl1} className="d-block w-100" alt="..."/>
            </div> */}
        </div>
    )
}


const Carousel = ({ recentActivityListTop4 }) => {
    console.log(recentActivityListTop4);
    return (
        <div className="container">
            <div id="Indexcarousel" className="carousel slide px-5" data-bs-ride="carousel">
                <div className="carousel-indicators">
                    <button type="button" data-bs-target="#Indexcarousel" data-bs-slide-to="0" className="active" aria-current="true" aria-label="Slide 1"></button>
                    <button type="button" data-bs-target="#Indexcarousel" data-bs-slide-to="1" aria-label="Slide 2"></button>
                    <button type="button" data-bs-target="#Indexcarousel" data-bs-slide-to="2" aria-label="Slide 3"></button>
                    <button type="button" data-bs-target="#Indexcarousel" data-bs-slide-to="3" aria-label="Slide 4"></button>
                </div>
                <div className="carousel-inner">
                    {recentActivityListTop4.map(activity => {
                        return <CarouselCard key={activity.ID} activity={activity} />
                    })}
                    {/* <div className="carousel-item active">
                        <p className="picName">name</p>
                        <img src={Pic} className="d-block w-100" alt="..."/>
                    </div>
                    <div className="carousel-item">
                        <p className="picName">name1</p>
                        <img src={Pic} className="d-block w-100" alt="..."/>
                    </div>
                    <div className="carousel-item">
                        <p className="picName">name2</p>
                        <img src={Pic} className="d-block w-100" alt="..."/>
                    </div>
                    <div className="carousel-item">
                        <p className="picName">name</p>
                        <img src={Pic} className="d-block w-100" alt="..."/>
                    </div> */}
                </div>
                <button className="carousel-control-prev" type="button" data-bs-target="#Indexcarousel" data-bs-slide="prev">
                    <span className="carousel-control-prev-icon" aria-hidden="true"></span>
                    <span className="visually-hidden">Previous</span>
                </button>
                <button className="carousel-control-next" type="button" data-bs-target="#Indexcarousel" data-bs-slide="next">
                    <span className="carousel-control-next-icon" aria-hidden="true"></span>
                    <span className="visually-hidden">Next</span>
                </button>
            </div>
        </div>
    );
  };

export default Carousel;

export const getStaticProps = async ctx => {
    const { data: getCarouselData } = await axiosCall({
        method: 'GET',
        url: 'https://ptx.transportdata.tw/MOTC/v2/Tourism/ScenicSpot?%24top=4&%24format=JSON',
    })

    const { data: recentActivityListTop4 } = await axiosCall({
    method: 'GET',
    url: `https://ptx.transportdata.tw/MOTC/v2/Tourism/Activity?$filter=date(StartTime) ge ${formatDate(
        new Date()
    )}&$orderby=StartTime asc&$top=4&$format=JSON`,
    })

    const { data: recentActivityList } = await axiosCall({
    method: 'GET',
    url: `https://ptx.transportdata.tw/MOTC/v2/Tourism/Activity?$filter=date(StartTime) ge ${formatDate(
        new Date()
    )}&$orderby=StartTime asc&$format=JSON`,
    })

    return {
    props: { getCarouselData, recentActivityListTop4, recentActivityList },
    }
}
