const Item = ({note, date, time, deleteItem})=> {

    function deleteData(){
        deleteItem(function (prevData) {
            return [
              ...prevData,
              {
                note,
                date,
                time,
              },
            ];
          }

        )
    }

    return <div className="item">
        <span>{note}</span>
        <p>{`${date} ${time}`}</p>
        <button className="remove" onClick={deleteData}>刪除</button>
    </div> 
}

export default Item;
