import * as React from 'react';
import { useState, useEffect } from "react";
import Carousel from "../../components/Common/Carousel";
import { requesScenicSpot, requesActivity, requesRestaurant } from "../../controller/apiManager";
import { useParams } from "react-router-dom";

async function fetchScenicSpotData(searchDetailData){ //載入頁面取後端資料
    const getdata = await requesScenicSpot('', {
        $top: 4,
        $format: 'JSON'
    });
    searchDetailData(getdata);
}

async function fetchRestaurantData(searchDetailData){ //載入頁面取後端資料
    const getdata = await requesRestaurant('', {
        $filter: "Picture/PictureUrl1 ne null",
        $top: 4,
        $format: 'JSON'
    });
    searchDetailData(getdata);
}

async function fetchActivityData(searchDetailData, id){ //載入頁面取後端資料
    const getdata = await requesActivity('', {
        $filter: `ActivityID eq '`+id+`'`,
        $format: 'JSON'
    });
    searchDetailData(getdata);
}


const SearchDetail = ({title}) => {
    const { itemId, itemType } = useParams();
    const [searchDetailData, setDetailData] = useState([]);

    useEffect(() => {
        if((itemType) === 'ActivityID'){
            fetchActivityData(setDetailData, itemId);
            console.log(searchDetailData);
        }

        if((title) === 'hotPlace'){
            fetchScenicSpotData(setDetailData);
        }

        if((title) === 'goodFood'){
            fetchRestaurantData(setDetailData);
        }
    }, []);



    return (
      <div>
          <Carousel/>
      </div>
    );
  };

  export default SearchDetail;
