import { useState, useEffect } from "react";
import ActivityCard from "./ActivityCard";
import PointCard from "./PointCard";
import requestAPI, { requesScenicSpot, requesActivity, requesRestaurant } from "../controller/apiManager";
// import API_GET_DATA from "../../../global/constants"

async function fetchScenicSpotData(sethotList){ //載入頁面取後端資料
    const getdata = await requesScenicSpot('', {
        $top: 4,
        $format: 'JSON'
    });
    sethotList(getdata);
}

async function fetchRestaurantData(sethotList){ //載入頁面取後端資料
    const getdata = await requesRestaurant('', {
        $filter: "Picture/PictureUrl1 ne null",
        $top: 4,
        $format: 'JSON'
    });
    sethotList(getdata);
}

async function fetchActivityData(sethotList){ //載入頁面取後端資料
    const getdata = await requesActivity('', {
        $filter: "Picture/PictureUrl1 ne null",
        $top: 4,
        $format: 'JSON'
    });
    sethotList(getdata);
}

const IntroList = ({ title, type }) => {
    const [hotList, sethotList] = useState([]);

    useEffect(() => {
        if((title) === 'newactivity'){
            fetchActivityData(sethotList);
            console.log(hotList);
        }

        if((title) === 'hotPlace'){
            fetchScenicSpotData(sethotList);
        }

        if((title) === 'goodFood'){
            fetchRestaurantData(sethotList);
        }
    }, []);


    const getCard=({ item, type })=>{
        if(type === 'AC'){
            return <ACard item={item}/>
        } else {
            return <PCard item={item}/>
        }
    }

    const ACard=({item})=>{
        const {ActivityName, Location, EndTime, StartTime, Picture} = item;
        return <ActivityCard 
            item={item}
        />;
    }

    const PCard=()=>{
        var output=[];
        for(let i=0;i<4;i++){
            output.push(<PointCard/>);
        }
        return output;
    }

    var titleName = {
        newactivity : '近期活動',
        hotPlace : '熱門打卡景點',
        goodFood : '一再回訪的美食',
        otherActivity: '還有這些不能錯過的活動',
        otherPlace: '還有這些不能錯過的景點',
        otherRestaurant: '還有這些不能錯過的美食'
    }
    return (
        <div className="container p-5" id="newActivityBlock">
            <div className="containerTitleBox">
                <h1>{titleName[(title)]}</h1>
                <span className="moreBtn red mr-5 position-relative">查看更多活動</span>
            </div>
            <div className="d-flex flex-wrap Block">
                { hotList.map((item) => {
                    return <gatCard item={item} type={type}/>
                }) }
            </div>
        </div>
    );
  };

export default IntroList;
