                                <option key={cityKey} value={cityKey}>
                                {CITY_OPTIONS[cityKey]}
                                </option>
                            ))} */}
                            <option value="ScenicSpotID">探索景點</option>
                            <option value="ActivityID">節慶活動</option>
                            <option value="RestaurantID">品嚐美食</option>
                        </select>
                    </div>
                    <input type="text" className="form-control mb-1" id="" placeholder="你想去哪裡？請輸入關鍵字"/>
                    <button type="button" className="btn btn-primary btnW100" onClick={saveSearchData}><i className="fas fa-search"></i>搜尋</button>
                </div>
            </div>
        </div>
    );
  };

export default Header;
