const IntroList = ({ title, type }) => {
    const [hotList, sethotList] = useState([]);
    // const [RestaurantData, setRestaurantData] = useState([]);
    // const [ActivityData, setActivityData] = useState([]);

    useEffect(() => {
        // if((title) === 'newactivity'){
        //     fetchActivityData(sethotList);
        // }

        // if((title) === 'hotPlace'){
        //     fetchScenicSpotData(sethotList);
        // }

        // if((title) === 'goodFood'){
        //     fetchRestaurantData(sethotList);
        // }
        const hotList = apiManager.requesScenicSpot("", {
            $filter: "Picture/PictureUrl1 ne null",
            $format: "JSON",
            $top: 4,
        });
        console.log(hotList)
        sethotList(hotList);
    }, []);


    const getCard=({ item, type })=>{
        if(type === 'AC'){
            return <ACard/>
        } else {
            return <PCard/>
        }
    }

    const ACard=()=>{
        var output=[];
        for(let i=0;i<4;i++){
            output.push(<ActivityCard/>);
        }
        return output;
    }

    const PCard=()=>{
        var output=[];
        for(let i=0;i<4;i++){
            output.push(<PointCard/>);
        }
        return output;
    }

    var titleName = {
        newactivity : '近期活動',
        hotPlace : '熱門打卡景點',
        goodFood : '一再回訪的美食',
        otherActivity: '還有這些不能錯過的活動',
        otherPlace: '還有這些不能錯過的景點',
        otherRestaurant: '還有這些不能錯過的美食'
    }
    return (
        <div className="container p-5" id="newActivityBlock">
            <div className="containerTitleBox">
                <h1>{titleName[(title)]}</h1>
                <span className="moreBtn red mr-5 position-relative">查看更多活動</span>
            </div>
            <div className="d-flex flex-wrap Block">
                { hotList.map((item) => {
                    return <gatCard item={item} type={type}/>
                }) }
            </div>
        </div>
    );
  };

export default IntroList;
