const Info = ({data, itemType}) => {
    const {Description, Class1} = data[0];

    const getTitle = (item) => {
        const { ActivityName, ScenicSpotName, RestaurantName } = data;
        return ActivityName || ScenicSpotName || RestaurantName;
    };

    const InfoTitle = () => {
        switch (itemType) {
            case "ActivityID":
                return '活動介紹';
            case "ScenicSpotID":
                return '景點介紹';
            default:
                return '餐廳介紹';
        }
    };

    return (
        <div className="container p-5" id="pageContent">
            <div className="pageTitleBox px-2">
                <h1 className="pageTitle">{getTitle(data)}</h1>
                <div className="d-flex py-2"> 
                    <span className="badge badgeB-warning badgeB">{Class1}</span>
                </div>
            </div>
            <div className="pageIntroBox p-2">
                <h3 className="subtitle">{InfoTitle}</h3>
                <p className="introBox">
                    {Description}
                </p>
            </div>    
        </div>
    );
};

export default Info;
