async function fetchScenicSpotData(setScenicSpotData){ //載入頁面取後端資料
    var data = apiManager.requesScenicSpot('', {
        $top: 4,
        $format: 'JSON'
    });
    setScenicSpotData(data);
}

async function fetchRestaurantData(setRestaurantData){ //載入頁面取後端資料
    var data = apiManager.requesRestaurant('', {
        $top: 4,
        $format: 'JSON'
    });
    // const res = await fetch();
    // const {data} = await res.json();
    setRestaurantData(data);
}

async function fetchActivityData(setActivityData){ //載入頁面取後端資料
    var data = apiManager.requesActivity('', {
        $top: 4,
        $format: 'JSON'
    });
    setActivityData(data);
}

const IntroList = ({ title }) => {
    const [ScenicSpotData, setScenicSpotData] = useState([]);
    const [RestaurantData, setRestaurantData] = useState([]);
    const [ActivityData, setActivityData] = useState([]);

