const DetailInfo = ({data, itemType}) => {
    console.log(data);
    const {StartTime, EndTime, Organizer, Location} = data[0];
    const ActStartTime = (StartTime).substr(0, 10).replace(/-/g, '/');
    const ActEndTime = (EndTime).substr(0, 10).replace(/-/g, '/');
    let activityfield = [StartTime, EndTime, Organizer, Location];

    if(itemType === 'ActivityID'){
        Name = item.RestaurantName;
        path = 'searchdetail/'+item.RestaurantID+ '/RestaurantID';
    } else if(itemType === 'ScenicSpotID') {
        Name = item.ScenicSpotName;
        path = 'searchdetail/'+item.ScenicSpotID+ '/ScenicSpotID';
    } else {
        Name = item.ActivityName;
        path = 'searchdetail/'+item.ActivityID+ '/ActivityID';
    }
    // const {} = data;
    const ActivityHtml = '<div className="info-item">'+
                            '<div className="info-item-title">開放時間</div>'+
                            '<div className="info-item-content">'+StartTime+' - '+EndTime+'</div>'+
                        '</div>'+
                        '<div className="info-item">'+
                            '<div className="info-item-title">主辦單位</div>'+
                            '<div className="info-item-content">'+Organizer+'</div>'+
                        '</div>'+
                        '<div className="info-item">'+
                            '<div className="info-item-title">活動地點</div>'+
                            '<div className="info-item-content">'+Location+'</div>'+
                        '</div>';

    const RestaurantHtml = '<div className="info-item">'+
                                '<div className="info-item-title">營業時間</div>'+
                                '<div className="info-item-content">'+OpenTime+'</div>'+
                            '</div>'+
                            '<div className="info-item">'+
                                '<div className="info-item-title">聯絡電話</div>'+
                                '<div className="info-item-content">'+Phone+'</div>'+
                            '</div>'+
                            '<div className="info-item">'+
                                '<div className="info-item-title">餐廳地址</div>'+
                                '<div className="info-item-content">'+Address+'</div>'+
                            '</div>'+
                            '<div className="info-item">'+
                                '<div className="info-item-title">官方網站</div>'+
                                '<div className="info-item-content">'+WebsiteUrl+'</div>'+
                            '</div>';

    const ScenicSpotHtml = '<div className="info-item">'+
                            '<div className="info-item-title">開放時間</div>'+
                            '<div className="info-item-content">'+OpenTime+'</div>'+
                        '</div>'+
                        '<div className="info-item">'+
                            '<div className="info-item-title">服務電話</div>'+
                            '<div className="info-item-content">'+Phone+'</div>'+
                        '</div>'+
                        '<div className="info-item">'+
                            '<div className="info-item-title">票價資訊</div>'+
                            '<div className="info-item-content">'+TicketInfo+'</div>'+
                        '</div>'+
                        '<div className="info-item">'+
                            '<div className="info-item-title">景點地址</div>'+
                            '<div className="info-item-content">'+Address+'</div>'+
                        '</div>';
    return (
        <div className="col-6 p-2">
            <div className="info-card">
                <div className="info-item">
                    <div className="info-item-title">開放時間</div>
                    <div className="info-item-content">{ActStartTime} - {ActEndTime}</div>
                </div>
                <div className="info-item">
                    <div className="info-item-title">主辦單位</div>
                    <div className="info-item-content">{Organizer}</div>
                </div>
                <div className="info-item">
                    <div className="info-item-title">活動地點</div>
                    <div className="info-item-content">{Location}</div>
                </div>
            </div>
        </div>
    );
};

export default DetailInfo;
