const Home = () => {
  const [data, setData] = useState([]);
  const submittingDataStaus = useRef(false);

  useEffect(() => {
      if(!submittingDataStaus.current){
          return;
      }
        fetchSetData(data).then(data => submittingDataStaus.current = false)
  }, [data]);

  useEffect(() => { 
    fetchData(setData);
    // fetch(API_GET_DATA)
    // .then(res => res.json())
    // .then(data => {
    //     console.log(data);
    // })
  }, [])

  return (
    <div className="app">
      <Edit add={setData} submittingDataStaus={submittingDataStaus}/>
      <List listData={data} deleteData={setData} submittingDataStaus={submittingDataStaus}/>
    </div>
  );
};

export default Home;
