import { useState } from "react";

const Edit = ({add})=> {

    const [note, setNote] = useState('');

    function changeNote(e){
        setNote(e.target,value);
    }

    function addItem (){
        add([1, 2, 3]);
    }

    return <div>
        <h1>備忘錄</h1>
        <p>記事：</p>
        <input type="text" value={note} onChange={changeNote}/>
        <p>日期：</p>
        <input type="date"/>
        <p>時間：</p>
        <input type="time"/>
        <button onClick={addItem} className="add">新增</button>
    </div> 
}

export default Edit;
