async function fetchScenicSpotData(setDetailData){ //載入頁面取後端資料
    const getdata = await requesScenicSpot('', {
        $top: 4,
        $format: 'JSON'
    });
    setDetailData(getdata);
}

async function fetchRestaurantData(setDetailData){ //載入頁面取後端資料
    const getdata = await requesRestaurant('', {
        $filter: "Picture/PictureUrl1 ne null",
        $top: 4,
        $format: 'JSON'
    });
    setDetailData(getdata);
}

function fetchActivityData(setDetailData, id){ //載入頁面取後端資料
    console.log('33');
